import {useState} from "react"; // Importing the useState hook from React
import PdfViewer from "pdf-viewer-reactjs"; // Importing the PdfViewer component from pdf-viewer-reactjs

/**
 * The PDFViewerComponent functional component
 *
 * This component is responsible for rendering a PDF viewer
 * and handling the loading state of the PDF document
 *
 * @param {string} pdfUrl - The URL of the PDF document to be rendered
 */
const PDFViewerComponent = ({pdfUrl}) => {
  /**
   * A state variable to track the loading state of the PDF document
   *
   * Initially set to true, indicating that the PDF is loading
   */
  const [isLoading, setIsLoading] = useState(true);

  return (
    <div>
      /** * Conditional rendering of a "Loading..." message * * If isLoading is
      true, render the message */
      {isLoading && <div>Loading...</div>}
      <PdfViewer
        /**
         * The document prop is an object with a single property, url
         *
         * The url property is set to the pdfUrl prop passed to the component
         */
        document={{
          url: pdfUrl,
        }}
        /**
         * The onLoad event handler is called when the PDF document is loaded
         *
         * Sets the isLoading state to false, indicating that the PDF is loaded
         */
        onLoad={() => setIsLoading(false)}
      />
    </div>
  );
};

export default PDFViewerComponent; // Exporting the PDFViewerComponent as the default export
