import React, {useState} from "react";
import {Button} from "react-bootstrap";
import "./Achievements.css";
const pdfPath = require("../rewards&recognition.pdf");
const Achievements = () => {
  const [showPdf1, setShowPdf1] = useState(false);
  const [showPdf2, setShowPdf2] = useState(false);

  const openPdf1 = () => {
    setShowPdf1(true);
  };

  const closePdf1 = () => {
    setShowPdf1(false);
  };

  const openPdf2 = () => {
    setShowPdf2(true);
  };

  const closePdf2 = () => {
    setShowPdf2(false);
  };

  return (
    <section id="achievements">
      <div className="heading">
        <Button variant="link" onClick={openPdf1}>
          <i className="fa fa-trophy" aria-hidden="true"></i>
        </Button>
        <Button variant="link" onClick={openPdf2}>
          <i className="fa fa-trophy" aria-hidden="true"></i>
        </Button>
        <h2>Achievements</h2>
      </div>
      {showPdf1 && (
        <div className="pdf-container">
          <embed
            src={pdfPath}
            type="application/pdf"
            width="100%"
            height="600px"
          />
          <Button variant="link" onClick={closePdf1}>
            Close
          </Button>
        </div>
      )}
      {showPdf2 && (
        <div className="pdf-container">
          <embed
            src={pdfPath2}
            type="application/pdf"
            width="100%"
            height="600px"
          />
          <Button variant="link" onClick={closePdf2}>
            Close
          </Button>
        </div>
      )}
    </section>
  );
};

export default Achievements;
