import React, {useState} from "react";
import {Button} from "react-bootstrap";
import ModalComponent from "./ModalComponent"; // Import your ModalComponent

const Achievements = () => {
  const [showModal, setShowModal] = useState(false);

  const handleClose = () => setShowModal(false);
  const handleShow = () => setShowModal(true);

  return (
    <section id="achievements">
      <div className="heading">            <i className="fa fa-trophy" aria-hidden="true"></i>

          <Button variant="link" onClick={handleShow}>
          </Button>
        <h2>Achievements</h2>
      </div>

      {/* Use the ModalComponent */}
      <ModalComponent showModal={showModal} handleClose={handleClose} />

      {/* Your achievement items */}
      <div className="achi">{/* Achievement items */}</div>
    </section>
  );
};

export default Achievements;
