import React from "react";
import "../components/About.css"; // Import the CSS file
import img from "./img.jpeg";

const About = () => (
  <section id="about">
    {/* <h2>Summary</h2> */}
    <div class="photo">
      <img src={img} alt="Description" />
    </div>
    <div class="text">
      <p>
      I am the <strong>Chief Technology Officer (CTO) at Starteryou Inc.</strong>, where I lead the vision and strategy for an innovative platform connecting teenagers and college students with meaningful part-time job opportunities. I oversee software development, ensuring our technology stack aligns with business objectives, and drive continuous improvement in product offerings. Our stack includes React, Vite, Node.js, MongoDB, AWS (EC2, Docker), and HTTPS security, emphasizing scalability and efficiency.

// Rest of the text remains the same
        <div class="paper">
          <p>
            Publication:{" "}
            <a href="https://link.springer.com/chapter/10.1007/978-3-031-53963-3_7">
              COVID-19 Vaccine Response on Social Media Using LDA Analysis
            </a>
          </p>
        </div>
        <div class="paper">
          <p>
            Certifications:{" "}
            <a href="https://github.com/nikshepkulli/niku/tree/master/MongoDB%20Certificates">
              MongoDB Certificates
            </a>{" "}
            |{" "}
            <a href="https://github.com/nikshepkulli/niku/tree/master/Forage%20Certificate">
              Forage Certificates
            </a>
          </p>
        </div>
      </p>
    </div>
  </section>
);

export default About;
