import React, {useState} from "react";
import {Button} from "react-bootstrap";
import ModalComponent from "./ModalComponent"; // Import your ModalComponent
import "./Achievements.css";
import PDFViewerComponent from "./PDFViewerComponent";

const Achievements = () => {
  const [showModal, setShowModal] = useState(false);

  const handleClose = () => setShowModal(false);
  const handleShow = () => setShowModal(true);
  const pdfUrl1 = "src/rewards&recognition.pdf";
  const pdfUrl2 = "src/rewards&recognition2.pdf";
  // Define your modal content as a separate JSX element
  const modalContent = (
    <div>
      <p>
        <div></div>
      </p>
      {/* Add any other content you want here */}
    </div>
  );

  return (
    <section id="achievements">
      <div className="heading">
        <p>
          <Button variant="link" onClick={handleShow}>
            <i className="fa fa-trophy" aria-hidden="true"></i>
          </Button>
        </p>
        <h2>Achievements</h2>
      </div>

      {/* Use the ModalComponent with modalTitle and modalContent props */}
      <ModalComponent
        showModal={showModal}
        handleClose={handleClose}
        modalTitle="Achievements"
        modalContent={modalContent}
      />

      {/* Your achievement items */}
      <div className="achi">{/* Achievement items */}</div>
    </section>
  );
};

export default Achievements;
