import React, {useState} from "react";
import {Document, Page} from "react-pdf";

const pdfUrl1 = "src/rewards&recognition.pdf";
const pdfUrl2 = "src/rewards&recognition2.pdf";

/**
 * The PDFViewerComponent functional component
 *
 * This component is responsible for rendering a PDF viewer
 * and handling the loading state of the PDF document
 *
 * @param {string} pdfUrl - The URL of the PDF document to be rendered
 */
const PDFViewerComponent = ({pdfUrl}) => {
  const [isLoading, setIsLoading] = useState(true);
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);

  const onLoadSuccess = ({numPages}) => {
    setIsLoading(false);
    setNumPages(numPages);
  };

  const onLoadError = (error) => {
    console.error(error);
  };

  return (
    <div>
      <Document
        file={pdfUrl}
        onLoadSuccess={onLoadSuccess}
        onLoadError={onLoadError}
      >
        <Page pageNumber={pageNumber} />
      </Document>
      <p>
        Page {pageNumber} of {numPages}
      </p>
    </div>
  );
};

export default PDFViewerComponent;
