import React, {useState} from "react";
import {Button} from "react-bootstrap";
import "./Achievements.css";
const pdfPath = "src\\rewards&recognition2.pdf";

const Achievements = () => {
  const [showPdf, setShowPdf] = useState(false);

  const togglePdf = () => {
    setShowPdf(!showPdf);
  };
return (
    <section id="achievements">
      <div className="heading">
        <Button variant="link" onClick={togglePdf}>
          <i className="fa fa-trophy" aria-hidden="true"></i>
        </Button>
        <h2>Achievements</h2>
      </div>
      {showPdf && (
        <div className="pdf-section">
          <embed
            src={pdfPath}
            type="application/pdf"
            width="100%"
            height="600px"
          />
        </div>
      )}
    </section>
  );

  return (
    <section id="achievements">
      <div className="heading">
        <Button variant="link" onClick={togglePdf}>
          <i className="fa fa-trophy" aria-hidden="true"></i>
        </Button>
        <h2>Achievements</h2>
      </div>
      {showPdf && (
        <embed
          src={pdfPath}
          type="application/pdf"
          width="100%"
          height="600px"
        />
      )}
    </section>
  );
};

export default Achievements;
