import PDFViewerComponent from "./PDFViewerComponent";
import {Button} from "react-bootstrap";
import "./Achievements.css";
import React, {useState} from "react"; // Added missing import statement

const Achievements = () => {
  const pdfUrl1 = "src/rewards&recognition1.pdf";
  const pdfUrl2 = "src/rewards&recognition2.pdf";
  const [showPdf1, setShowPdf1] = useState(false);
  const [showPdf2, setShowPdf2] = useState(false);

  const handlePdf1Click = () => {
    setShowPdf1(true);
    setShowPdf2(false);
  };

  const handlePdf2Click = () => {
    setShowPdf1(false);
    setShowPdf2(true);
  };

  return (
    <div>
      <Button variant="outline-primary" onClick={handlePdf1Click}>
        Show PDF 1
      </Button>
      <Button variant="outline-primary" onClick={handlePdf2Click}>
        Show PDF 2
      </Button>
      <PDFViewerComponent pdfUrl={pdfUrl2}></PDFViewerComponent>
    </div>
  );
};

export default Achievements;
